// ignore_for_file: require_trailing_commas

import 'package:checks/checks.dart';
import 'package:flutter/widgets.dart';
import 'package:spot/spot.dart';

/// Matchers for [RobinHood] auto-generated by spot
///
/// ```dart
/// spot<RobinHood>().printMatchers();
/// ```
extension RobinHoodMatcher on WidgetMatcher<RobinHood> {
  WidgetMatcher<RobinHood> hasPowerWhere(MatchProp<Power> match) {
    return hasProp<Power>('power', match);
  }
  
  WidgetMatcher<RobinHood> hasPower(Power value) {
    return hasProp<Power>('power', (it) => it.equals(value));
  }

}

extension RobinHoodSelector on WidgetSelector<RobinHood> {
  WidgetSelector<RobinHood> withPowerMatching(MatchProp<Power> match) {
    return withProp<Power>('power', match);
  }
  
  WidgetSelector<RobinHood> withPower(Power value) {
    return withProp<Power>('power', (it) => it.equals(value));
  }

}
    
    